import React, { useState } from "react";
import './ExpenseForm.css'

const ExpenseForm = (props) => {
    const [expenseTitle, setExpenseTitle] = useState('');
    const [amount, setAmount] = useState('');
    const [date, setDate] = useState('');

    const expenseTitleChange = (event) => {
        setExpenseTitle(event.target.value)
    }
    const amountChange = (event) => {
        setAmount(event.target.value)
    }
    const dateChange = (event) => {
        setDate(event.target.value)
    }

    const onSubmit = (event) => {
        event.preventDefault();

        const expenseData = {
            title: expenseTitle,
            amount: amount,
            date: Date(date)
        }

        props.saveExpenseData(expenseData)
        setAmount('')
        setExpenseTitle('')
        setDate('')
    }

    return (
        <form onSubmit={onSubmit}>
            <div className="new-expense__controls">
                <div className="new-expense__control">
                    <label>Title</label>
                    <input type = 'text' onChange = {expenseTitleChange} value = {expenseTitle}/>
                </div>
                <div className="new-expense__control">
                    <label>Amount</label>
                    <input type = 'number' min='0.01' step='0.01' onChange = {amountChange} value={amount}/>
                </div>
                <div className="new-expense__control">
                    <label>Date</label>
                    <input type = 'date' min='2019-01-01' max='2022-12-31' onChange = {dateChange} value = {date}/>
                </div>
            </div>
            <div className="new-expense__actions">
                <button type="submit">Add Expense</button>
            </div>
        </form>
    )
}

export default ExpenseForm;
